INSERT INTO widgets_info (id, title, description, info_image_id, footer) VALUES ( 6, 'Criticality Legend', 'Criticality: numerical index of the severity of a failure or a fault combined with the probability or frequency of its occurrence. In the context of Health Index, the numerical index will be translated in Low, Medium, High levels and applied at case level only.', 201, NULL ) ON CONFLICT (id) DO NOTHING;
