INSERT INTO widgets_info (id, title, description, info_image_id, footer) VALUES ( 9, NULL, 'Overview of total number of TRP cases with their investigation status. The graph shows the active cases and their status is classified as: Analyse: the analysis of the causes is ongoing and the corrective actions are under definition Apply: the corrective actions are available and their implementation is ongoing or to be started Validate: the corrective actions are implemented and their field validation is ongoing', NULL, NULL ) ON CONFLICT (id) DO NOTHING;
