INSERT INTO widgets_info (id, title, description, info_image_id, footer) VALUES ( 15, NULL, 'Info about latest available thrust load calculation for 4B bearing. The widget is also reporting the current HP recoup orifice installed size, if the info has been shared by site and then tracked in iCenter database. If a warning icon is displayed, an iCenter case should be requesting an orifice change on this unit; please refer to Thrust bearing load app for further details', NULL, NULL ) ON CONFLICT (id) DO NOTHING;
