INSERT INTO widgets_info (id, title, description, info_image_id, footer) VALUES ( 23, NULL, 'Summary of the health status at the selected asset level (project, plant, train, lineup); the indicated health status (good, normal, bad) is the worst one among the machine subsystems available in the selected asset. Please refer to the Health index app for further details.', NULL, NULL ) ON CONFLICT (id) DO NOTHING;
INSERT INTO widgets_info (id, title, description, info_image_id, footer) VALUES ( 24, NULL, 'Health index of all subsystems of the selected asset; the health index is a numerical assessment from 0 (worst condition) to 10 (no iCenter cases opened), made through fuzzy logic, that is leveraging all iCenter cases in open status and linked to each subsystem; the numerical health index is then converted in a three color Health status [Green=Monitor/Acceptable; Yellow=Schedule; Red=Intervene] that gives an idea about the care that each subsystem is needing', NULL, NULL ) ON CONFLICT (id) DO NOTHING;
